const gids = [];

// get all the unique Asana task gids
for (item of items) {
  var gid = parseInt(item.json.resource.gid);
  var resource_type = item.json.resource.resource_type;
  if (!(gids.includes(gid)) && resource_type == "task") {
    gids.push(gid);
  }
}

// show in output
const new_items = [];
for (gid of gids) {
  var new_item = {
    "json": {
      "gid": 0,
      "gids": [],
      "notionfilter": ""
    }
  };
  new_item = JSON.stringify(new_item);
  new_item = JSON.parse(new_item);
  new_item.json.gid = gid;
  new_item.json.gids = gids;
  new_items.push(new_item);

  // Notion filter
  notionfilter = {
    or: [],
  }

  for (gid of gids) {
    const filter = {
      property: 'Asana GID',
      number: {
        equals: gid
      }
    }
    notionfilter["or"].push(filter);
  }


  new_item.json.notionfilter = JSON.stringify(notionfilter); 
}

console.log(gids);
return new_items;

const gids_to_update = [];
const database_ids = [];

for (item of $items("Find tasks")) {
  gids_to_update.push(parseInt(item.json.property_asana_gid));
  database_ids.push(item.json.id);
}
console.log(gids_to_update);
console.log(database_ids);

var gid;
let i = 0;
for (item of $items("Get tasks")) {
  gid = parseInt(item.json.gid);
  if (gids_to_update.includes(gid)) {
    item.json.action = "Update"
    item.json.database_id = database_ids[i];
  } else {
    item.json.action = "Create"
  }
  i++;
}

return $items("Get tasks");
