// aggregate `amounts_captured` with the customer, taking note 
// that `aggregateAmountsPerContact` is the value in cents
const aggregateAmountsPerContact = new Object();
for (const item of $input.all()) {
  if (aggregateAmountsPerContact[item.json.email] == null) {
    aggregateAmountsPerContact[item.json.email] = 0;
  }
  aggregateAmountsPerContact[item.json.email] += item.json.amount_captured;
}

// parse the data in a way that is usable in future nodes, and
// converts amounts from cents to dollars
const parsed = [];
for (const contact of Object.keys(aggregateAmountsPerContact)) {
    parsed.push({
        email: contact,
        amount_captured: aggregateAmountsPerContact[contact] / 100
    });
}

return parsed;
