parameters.jsCode:
function formatStdoutAsHtmlList(stdoutData) {

    // Split the stdout into lines and map to HTML list items
    const htmlListItems = stdoutData.split('\n').map((line) => {
        if (line.trim() && line !== "Listing...") { // Optionally skip empty lines or headers
            return `<li>${line.trim()}</li>`;
        }
    }).filter(item => item); // Remove any undefined items due to empty lines or skipped headers

    // Wrap the list items in a <ul> tag
    const htmlList = `<ul>${htmlListItems.join('')}</ul>`;

    // Return the formatted HTML list as part of an object
    return { "htmlList": htmlList };
}

return formatStdoutAsHtmlList($input.first().json.stdout);
