// Initialize empty arrays for both data sources
let productData = [];
let redirectData = [];

try {
  productData = $items("Data to Keep4");
} catch (error) {
  console.log("Error fetching product data:", error.message);
}

try {
  redirectData = $items("Data to Keep3");
} catch (error) {
  console.log("Error fetching redirect data:", error.message);
}

const mergedItems = [];

for (let i = 0; i < productData.length; i++) {
  const product = productData[i].json;

  const mergedItem = {
    name: product.name,
    tagline: product.tagline,
    description: product.description,
    next_url: null
  };

  if (i < redirectData.length && redirectData[i] && redirectData[i].json) {
    let url = redirectData[i].json.next_url;
    // Remove ?ref=producthunt from the URL
    if (url && url.includes('?ref=producthunt')) {
      url = url.replace('?ref=producthunt', '');
    }
    mergedItem.next_url = url;
  }

  mergedItems.push({ json: mergedItem });
}

console.log(`Product data items: ${productData.length}`);
console.log(`Redirect data items: ${redirectData.length}`);
console.log(`Merged items: ${mergedItems.length}`);

return mergedItems;
