const rawData = $json["text"];
const cleanedData = rawData
  .replace(/(\r\n|\n|\r)/gm, " ")   // remove line breaks
  .trim()                           // remove extra spaces
  .replace(/[^\w\s]/gi, "");         // remove special characters
return { cleanedData };

const userQuery =  $('Chat Message Trigger').first().json.chatInput
// Retrieve the user query from the previous node output.
// Assuming the Pinecone node has passed an array of items where each item has a document and score:
let documents = items.map(item => {
  return {
    pageContent: item.json.document.pageContent,
    score: item.json.score
  };
});

// Sort the documents by their score in descending order.
documents.sort((a, b) => b.score - a.score);

// Pick the top 3 documents to use as context.
const topDocuments = documents.slice(0, 3);

// Combine the top documents into one context string.
const contextContent = topDocuments
  .map((doc, index) => `Document ${index + 1}:\n${doc.pageContent}`)
  .join("\n\n");

// Build the final prompt that combines the context with the user query.
const prompt = `Using the following context from documents:\n\n${contextContent}\n\nAnswer the following question:\n${userQuery}\n\nAnswer:`;

// Return the prompt so it can be passed to a Chat/AI node for further processing.
return [{ json: { prompt } }];
