const rawContent = $json.output;

// 1. Replace all line breaks with spaces
let cleaned = rawContent.replace(/\n/g, ' ');

// 2. Trim any extra spaces at the beginning and end
cleaned = cleaned.trim();

// 3. Escape backslashes and double quotes for JSON safety
let escaped = cleaned.replace(/\\/g, '\\\\').replace(/"/g, '\\"');

// 4. Remove leading or trailing escaped quotes if accidentally included
escaped = escaped.replace(/^\\\"/, '').replace(/\\\"$/, '');

// 5. Return the cleaned and fully escaped string
return [
  {
    json: {
      escapedString: escaped
    }
  }
];
