let message = inputData.message; // replace 'message' with the correct field name

let messageContent = '';

if (message && message.text) {
  messageContent = message.text;
}

return [
  {
    json: {
      messageContent: messageContent
    }
  }
];

let inputData = items[0].json; // get the input data
let variableContent = String(inputData.messageContent || ''); // replace 'variable' with the correct field name, convert to string and assign empty string if undefined

let regex = /#(\w+)\b(.*)/; // regex to match hashtag word and rest of the content
let matches = regex.exec(variableContent);

let extractedContent = '';
let remainingContent = '';

if (matches !== null) {
  extractedContent = matches[1];
  remainingContent = matches[2].trim();
} else {
  remainingContent = variableContent.trim();
}

return [
  {
    json: {
      extractedContent: extractedContent,
      remainingContent: remainingContent    }
  }
];

let inputData = items[0].json; // get the input data
let outputData = [];

for(let key in inputData) {
    if(inputData.hasOwnProperty(key)) {
        let itemKey = key;
        let itemValue = inputData[key];

        for(let subKey in itemValue) {
            if(itemValue.hasOwnProperty(subKey)) {
                let formattedItem = {
                    'Item': itemKey,
                    'SubItem': subKey,
                    'Value': itemValue[subKey]
                };

                outputData.push({json: formattedItem});
            }
        }
    }
}

return outputData;

let outputData = items.map(item => {
    let data = item.json.Value;

    return {
        json: {
            'name': data.name,
            'audience_size_lower_bound': data.audience_size_lower_bound,
            'audience_size_upper_bound': data.audience_size_upper_bound,
            'path': data.path,
            'description': data.description,
            'topic': data.topic
        }
    };
});

return outputData;

